import axios from "axios";
import { set } from "react-native-reanimated";
import { setAppDetails } from "@/redux/appSlice";
import CustomLoader from "@/components/CustomLoader";
export default function index() {
  const width = Dimensions.get("window").width;
  const [isLoading, setIsLoading] = useState(false);
  const [data, setData] = useState(null);
  const [notavailable, setNotAvailable] = useState(false);
  const [notListed, setNotListed] = useState(false);
  const [reload, setReload] = useState(false);
  const toggleNotListedModal = () => setNotListed(!notListed);
  const [refreshing, setRefreshing] = useState(false);

  const dispatch = useDispatch();
  const [bannerImage, setBannerImage] = useState([]);
  const [locationData, setLocationData] = useState(null);

  const [selectedLocation, setSelectedLocation] = useState("Delhi");

  const handleLocationChange = (location) => {
    setSelectedLocation(location);
  };
  var count = 0;

  const getAppData = async () => {
    // TODO, tanstackquery
    try {
      const response = await axios.get(`${BACKEND}/api/v1/app/getApp`);
      // console.log("getAPpData called ", count++);
      // console.log("ðŸš€ ~ getAppData ~ response:", response.data.data.locations)
      // console.log(response.data.data)

      setBannerImage(response.data.data.Banner);
      setLocationData(response.data.data.locations);

      AsyncStorage.setItem(
        "RegistrationFee",
        JSON.stringify(response.data.data.RegistrationFee)
      );
    } catch (error) {
      setBannerImage([]);
      console.error("Failed to fetch banner image data:", error);
    }
  };
  //TODO tanstackquery , redux save in Appslice

  dispatch(setAppDetails(locationData || []));

  const getUserData = async () => {
    const res = await AsyncStorage.getItem("userData");
    // const data = JSON.parse(res);
    return res;
  };

  useEffect(() => {
    getAppData();

    const fetchLibraryDate = async () => {
      const res = await getUserData();
      // console.log("User Data:", res);
      dispatch(setUserDetails(res));

      setIsLoading(true);
      setReload(false);

      // console.log("Selected Location:109", selectedLocation);
      try {
        const fetchedData = await fetchRoomData({ selectedLocation });
        setData(fetchedData || []);
        // console.log("Fetched Data:________", fetchedData);
      } catch (error) {
        console.error("Failed to fetch room data:", error);
        setData(null);

        setNotAvailable(true);
        // Handle the error as needed, e.g., set an error state, show a message, etc.
        // For example, setError("Failed to load data. Please try again later.");
      } finally {
        setIsLoading(false);
      }
    };
    // getTokenAndPrintIt();

    fetchLibraryDate();
  }, [reload, selectedLocation]);

  const [assets, error] = useAssets([
    require("../../assets/icons/setting-5.svg"),
    require("../../assets/icons/arrow-3.svg"),
    require("../../assets/icons/arrow-down.svg"),
    require("../../assets/icons/arrow-down.svg"),
    require("../../assets/images/slider1.png"),
    require("../../assets/icons/hello.svg"),
    require("../../assets/icons/locationcard.svg"),
  ]);

  // console.log("-------------->", userDetails);

  //card listedrooms
  const renderItem = ({ item }) => (
    <TouchableOpacity
      style={{
        borderRadius: 15,
        borderWidth: 1.5,
        borderColor: "#dcd8da",
        marginBottom: 4,
        padding: 2,
      }}
      key={item._id}
      onPress={
        item.approved
          ? () =>
            router.push({
              pathname: "/(routes)/card-details",
              params: { item: JSON.stringify(item) },
            })
          : () => toggleNotListedModal()
      }
    // onPress={()=> {
    //   setNotListed(true)

    // }}
    >
      {item.approved && (
        <View style={styles.card}>
          <Image
            source={{
              uri:
                item?.images[0] ||
                "data:image/jpeg;base64,",
            }}
            style={{
              width: "auto",
              height: 100,
              borderRadius: 10,
              aspectRatio: 16 / 15,
            }}
          />
          <View
            style={{
              flex: 1,
              flexDirection: "column",
              // width: 200,
              marginLeft: 0,
              // justifyContent: "space-between",
            }}
          >
            <View
              style={{
                flex: 1,
                flexDirection: "column",
              }}
            >
              <Text
                style={{
                  fontSize: 17.34,
                  fontWeight: "500",
                  lineHeight: 26.01,
                  textAlign: "left",
                }}
              >
                {item.name.split(" ").slice(0, 3).join(" ")}
              </Text>
              <Text
                numberOfLines={2}
                style={{
                  fontSize: 12.14,
                  fontWeight: "400",
                  lineHeight: 20.21,
                  textAlign: "auto",
                  color: "#626262",
                }}
              >
                {item.shortDescription}
              </Text>
            </View>

            <View
              style={{
                flex: 1,

                height: 10,
                flexDirection: "row",
                // paddingRight: 20,
                justifyContent: "space-between",
                alignItems: "center",
              }}
            >
              {item.approved && (
                <View>
                  {item?.ratings ? (
                    <View
                      style={{
                        flexDirection: "row",
                        alignItems: "center",
                        gap: 5,
                      }}
                    >
                      <StarRating rating={item?.ratings} />
                      <Text>{item?.ratings}</Text>
                    </View>
                  ) : (
                    <View
                      style={{
                        flexDirection: "row",
                        alignItems: "center",
                        gap: 5,
                      }}
                    >
                      <StarRating rating={3} />
                      <Text>3.0</Text>
                    </View>
                  )}
                </View>
              )}

              {item.approved && (
                <View
                  style={{
                    flexDirection: "row",
                    gap: 3,
                    padding: 4,
                    justifyContent: "center",
                    alignItems: "center",
                  }}
                >
                  {assets && assets[6] && (
                    <Image
                      source={assets[6]}
                      style={{
                        width: 15,
                        height: 20,
                      }}
                    />
                  )}

                  <Text
                    style={{
                      fontSize: 12.14,
                      fontWeight: "400",
                      lineHeight: 18.21,
                      textAlign: "auto",
                      color: "#1E1E1E",
                    }}
                  >
                    {item.distance || "2KMs"}
                  </Text>
                </View>
              )}
            </View>
          </View>
        </View>
      )}
    </TouchableOpacity>
  );
  const [stickyHeight, setStickyHeight] = useState(0);
  const scrollViewRef = useRef(null);

  const handleLayout = (event: any) => {
    setStickyHeight(event.nativeEvent.layout.height);
  };

  const onRefresh = useCallback(() => {
    setRefreshing(true);
    getAppData();

    setTimeout(() => {
      setRefreshing(false);
    }, 2000);
  }, []);

  const userDetails = useSelector((state: any) => state.user);

  const userData = JSON.parse(userDetails.details)?.data?.username;
  // console.log("ðŸš€ ~ index ~ userData:", userData)

  return (
    <SafeAreaView
      style={{
        flex: 1,
        flexDirection: "column",
        gap: 5,
        // justifyContent: "center",
        // alignItems: "flex-start",
        backgroundColor: "white",
      }}
    >
      <View style={{ marginTop: 0 }}>
        <Header color="black" handleLocationChange={handleLocationChange} />
      </View>

      <NotListedModal isVisible={notListed} onClose={toggleNotListedModal} />

      {/* ///carousel -> Listings -> Filters */}

      <ScrollView
        style={{ flex: 1, gap: 40 }}
        stickyHeaderIndices={[1]}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        {/* Carousel */}
        <View style={styles.carousel}>
          <View style={styles.welcome}>
            <Text
              style={{
                fontSize: 25,
                fontWeight: "bold",
                color: "black",
              }}
            >
              Welcome,
              <Text
                style={{
                  fontSize: 25,
                  fontWeight: "bold",
                  color: "#0077B6",
                }}
              >
                {userData ? userData?.split(" ")[0] : "Board"}ðŸ‘‹
              </Text>
            </Text>
          </View>

          <Carousel
            loop
            width={width}
            height={height / 3}
            autoPlay={true}
            data={bannerImage}
            scrollAnimationDuration={2500}
            // onSnapToItem={(index) => console.log("current index:", index)}

            renderItem={({ item, index }) => (
              <View
                style={{
                  flex: 1,
                  // borderWidth: 1,

                  justifyContent: "center",
                  alignItems: "center",

                  padding: 30,
                }}
              >
                {bannerImage.length === 0 ? (
                  assets &&
                  assets[4] && (
                    <Image
                      source={assets[4]}
                      style={{
                        marginTop: -50,
                        width: width * 0.95,
                        height: height * 0.25,
                        borderRadius: 20,
                        // borderTopLeftRadius: 40,
                        // borderTopRightRadius: 40,
                      }}
                    />
                  )
                ) : (
                  <Image
                    source={item}
                    style={{
                      marginTop: -50,
                      width: width * 0.95,
                      height: height * 0.25,
                      borderRadius: 20,
                      objectFit: "cover",
                      // overflow: 'hidden'
                      // borderTopLeftRadius: 40,
                      // borderTopRightRadius: 40,
                    }}
                  />
                )}
              </View>
            )}
          />
        </View>

        <TouchableOpacity
          onPress={() =>
            router.push({
              pathname: "/(routes)/nearby",
              params: { data: JSON.stringify(data?.data) },
            })
          }
        >
          <View
            style={{
              marginVertical: 8,
              paddingHorizontal: 16,
              flexDirection: "row",
              alignItems: "center",
              justifyContent: "space-between",
            }}
          >
            <Text
              style={{
                fontSize: 18,
                fontWeight: "600",
                color: "black",
              }}
            >
              Near By
            </Text>
            <Ionicons
              name={"chevron-forward-outline"}
              size={18}
              color="blue"
              style={{ fontWeight: "600" }}
            />
          </View>
        </TouchableOpacity>

        {/* Lib Cards */}

        {isLoading ? (
             <CustomLoader visible={true} />
        ) : (
          <>
            <View style={{ paddingHorizontal: 16, marginTop: 4 }}>
              {data &&
                data?.data?.map((item, index) => renderItem({ item, index }))}

              {data?.data?.length == 0 && (
                <TouchableOpacity onPress={() => setReload(true)}>
                  <Text
                    style={{
                      textAlign: "center",
                      paddingTop: 50,
                      fontSize: 25,
                      color: "red",
                    }}
                  >
                    No listings available at the moment.
                  </Text>
                </TouchableOpacity>
              )}
            </View>

            {/* <View style={{ height: 900, width: 45 }}></View> */}
          </>
        )}
      </ScrollView>
    </SafeAreaView>
  );
}

